/**
 * Render
 *
 * @param {"home" | "article"} type - Detect config type, must be "home" or "article"
 * @param {string} url - Absolute route url
 *
 * return {object}
 */
export default async function render(type, url = '') {
  const files = {};

  ejs.fileLoader = path => files[path];
  const includer = name => {
    return {name, template: files[name]}
  };

  templateString.split('<!--/').slice(1).forEach(e => {
    const [name, template] = e.split('.ejs-->');
    files['/' + name] = template;
  });

  await connect();

  const meta = await getMetadata('davidsdevel', data.main.metadata)
  console.log(meta);
  const seo = generateSeoData(url, 'home', {blog: meta.blog});
  console.log(seo)

  return new Promise((resolve, reject) => {
    ejs.renderFile('/main', meta, {includer}, (err, html) => {
      if (err) {
        console.log(err);
        return {}
      }


      resolve({
        html,
        css,
        js,
        externalCSS: data.main.styles.externals,
        externalJS: data.main.scripts.externals,
        seo
      });
    });

  });
}
